  `sharable_enterprise_users` json NULL COMMENT '可以分享的用户，json列表  ',
  `sharable_role_switch` int NOT NULL DEFAULT 1 COMMENT '分享权限控制开关，默认是1关闭，0是开启',
  `sharable_sharing_role` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '可以分享的权限,  \"owner\"  / \"operator\" /  \"usabel\" /  \"reviewer\" ',
  `auto_logout_switch` int NOT NULL DEFAULT 1 COMMENT '自动登出开关',
  `auto_logout_interval` bigint NULL DEFAULT NULL,
  `auto_logout_time_unit` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '自动登出时间类型，year,month,day,hour,minute,second',
  `ip_restriction_switch` int NOT NULL DEFAULT 1 COMMENT 'ip限制开关',
  `ip_whitelist` json NULL COMMENT 'ip限制内容，json列表',
  `ip_blacklist` json NULL COMMENT 'ip限制内容，json列表',
  `account_locking_policy_switch` int NOT NULL DEFAULT 1 COMMENT '账号锁定策略开关',
  `account_locking_policy_number` int NULL DEFAULT NULL COMMENT '账号锁定策略次数',
  `account_locking_time_switch` int NOT NULL DEFAULT 1 COMMENT '账号锁定时间开关',
  `account_locking_time` int NULL DEFAULT NULL COMMENT '账号锁定时间，分钟',
  `custom_id` int NOT NULL DEFAULT 1 COMMENT '自定义ID开关，1关闭0打开',
  `created_user_id` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `created_date` datetime NOT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_user_id` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `updated_date` datetime NOT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `component_download_switch` int NOT NULL DEFAULT 0 COMMENT '组件下载开关，默认打开',
  `component_download_role` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT 'reviewer' COMMENT '组件下载角色，默认可编辑',
  `component_url_switch` int NOT NULL DEFAULT 0 COMMENT '组件url开关，默认打开',
  `component_url_role` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT 'operator' COMMENT '组件url角色，默认可编辑',
  `feedback_problem_switch` int NULL DEFAULT NULL COMMENT '反馈问题开关',
  `feedback_problem_text` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '反馈问题文本',
  `external_link_analysis` int NULL DEFAULT 1 COMMENT '外链模式流程分析',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `enterprise_id`(`enterprise_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 237 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = DYNAMIC;

