  `zh_hk` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '繁体香港',
  `en_us` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '英文',
  `ja_jp` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '日文',
  `ko_kr` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '韩文',
