DROP TABLE IF EXISTS `tb_workspace_connection`;
CREATE TABLE `tb_workspace_connection`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `workspace_id` bigint NOT NULL COMMENT '工作空间id',
  `db_type` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '数据库类型',
  `schema` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '模式',
  `url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT 'jdbc连接信息',
  `db_name` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '数据库名',
  `host` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '数据库连接主机地址',
  `port` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '数据库连接端口',
  `user` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '连接数据库用户名',
  `password` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '连接数据库密码',
  `user_remember` int NULL DEFAULT 0 COMMENT '1：记住用户，2：记住密码，3：记住用户&密码',
  `created_date` datetime NOT NULL COMMENT '创建时间',
  `created_user_id` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `updated_date` datetime NOT NULL COMMENT '更新时间',
  `updated_user_id` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_workspace_id`(`workspace_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 100002790 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '连接信息表' ROW_FORMAT = DYNAMIC;

