  `table_name` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '表展示名',
  `table_pre_name` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '记录更改之前的表名',
  `workspace_id` bigint NULL DEFAULT NULL COMMENT '工作区id',
  `original_table` int NULL DEFAULT NULL COMMENT '是否为原始表',
  `import_source` int NULL DEFAULT NULL COMMENT '来源，1表示csv上传，2表示数据库导表',
  `file_type` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '文件类型',
  `modified_time` datetime NULL DEFAULT NULL COMMENT '表单最近一次加载成功时对应的修改时间',
