-- ----------------------------
-- Function structure for generateBody
-- ----------------------------
DROP FUNCTION IF EXISTS `generateBody`;
delimiter ;;
CREATE FUNCTION `generateBody`(body TEXT)
 RETURNS text CHARSET utf8mb4 COLLATE utf8mb4_general_ci
BEGIN
    DECLARE idx int default 0;
    DECLARE jsonBody json default '{}';
    DECLARE `key` TEXT;
    DECLARE value TEXT;
    if (body is null or body = '') then
        RETURN null;
else
        while idx < json_length(body)
            do
                set `key` = json_unquote(json_extract(body, concat('$[', idx, '].name')));
                set value = json_unquote(json_extract(body, concat('$[', idx, '].value')));

                set jsonBody = json_merge(jsonBody, if(json_valid(value), json_object(`key`, json_extract(value, '$')),
                                                       json_object(`key`, value)));
                set idx = idx + 1;
end while;
RETURN json_pretty(jsonBody);
end if;
END
;;
