-- ----------------------------
DROP FUNCTION IF EXISTS `getEmailName`;
delimiter ;;
CREATE FUNCTION `getEmailName`(email TEXT)
 RETURNS text CHARSET utf8mb4 COLLATE utf8mb4_general_ci
BEGIN
    DECLARE start INT default 1;
    DECLARE len INT;

    if (email is null or email = '') then
        RETURN email;
end if;

    SET len = LOCATE('@', email) - 1;
    if len <= 0 THEN
        return email;
ELSE
        RETURN SUBSTRING(email, start, len);
end if;
END
;;
