-- ----------------------------
-- Function structure for getUrlParams
-- ----------------------------
DROP FUNCTION IF EXISTS `getUrlParams`;
delimiter ;;
CREATE FUNCTION `getUrlParams`(url TEXT)
 RETURNS json
BEGIN
    DECLARE urlPath TEXT;
    DECLARE paramsStr TEXT;
    DECLARE paramStr TEXT;
    DECLARE paramKey TEXT;
    DECLARE paramValue TEXT;
    DECLARE paramTagIdx int;
    DECLARE result json default '[]';
    DECLARE idx int default 1;
    SET urlPath = getUrlPath(url);
    SET paramsStr = SUBSTRING(url, LENGTH(urlPath) + 2);
    if (paramsStr is null or paramsStr = '') then
        RETURN '[]';
else
        while (true)
            do
                set paramStr = fn_split(paramsStr, '&', idx);
                if paramStr is null then
                    return result;
end if;

                Set paramTagIdx = locate('=', paramStr);
                if (paramTagIdx > 0) then
                    Set paramKey = substr(paramStr, 1, paramTagIdx - 1);
                    Set paramValue = substr(paramStr, paramTagIdx + 1);
else
                    Set paramKey = paramStr;
                    Set paramValue = '';
end if;
                set result = json_array_insert(result, '$[0]',
                                               json_set(jsonTemplate('param_node'), '$.name', paramKey, '$.value',
                                                        paramValue));
                set idx = idx + 1;
end while;
return result;
end if;
END
;;
delimiter ;

-- ----------------------------
-- Function structure for getUrlPath
-- ----------------------------
DROP FUNCTION IF EXISTS `getUrlPath`;
delimiter ;;
CREATE FUNCTION `getUrlPath`(url TEXT)
 RETURNS text CHARSET utf8mb4 COLLATE utf8mb4_general_ci
BEGIN
    DECLARE start INT default 1;
    DECLARE len INT;
    SET len = LOCATE('?', url) - 1;
    if len <= 0 THEN
        return url;
ELSE
        RETURN SUBSTRING(url, start, len);
end if;
END
;;
